INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (2, 'Genesis', 'The Sega Genesis, known as the Mega Drive outside North America, is a 16-bit home video game console developed and sold by Sega. The Genesis is Sega''s third console and the successor to the Master System. Sega released it as the Mega Drive in Japan in 1988, and later as the Genesis in North America in 1989.', 17990, 1, 2);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (5, 'NES Zapper', 'The NES Zapper, also known as The Light Gun or Beam Gun in Japan, is an electronic light gun accessory for the Nintendo Entertainment System (NES) and the Japanese Famicom. It was released in Japan for the Famicom on February 18, 1984 and alongside the launch of the NES in North America in October 1985.', 189990, 3, 1);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (6, 'Game Boy Camera', 'The Game Boy Camera (GBC), released as Pocket Camera in Japan, is a Nintendo accessory for the handheld Game Boy game console. It was released on February 21, 1998 in Japan, and manufacturing was ceased in late 2002. As a toy for user-generated content, it can be used to shoot grayscale photographs, edit them or create original drawings, and transfer images between GBC units or to the 64DD art game suite Mario Artist.', 2490, 3, 1);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (1, 'NES', 'The Nintendo Entertainment System (NES) is an 8-bit third-generation home video game console produced, released, and marketed by Nintendo. It is a remodelled export version of the company''s Family Computer (FC) platform in Japan, commonly known as the Famicom, which was launched on July 15, 1983.', 19990, 1, 1);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (4, 'Duck Hunt', 'Duck Hunt is a 1984 light gun shooter video game developed and published by Nintendo for the Nintendo Entertainment System (NES) video game console. The game was first released in Japan in April 1984, and was released as a launch game for the NES in North America in October 1985, with it also releasing in Europe two years later.', 6790, 2, 1);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (3, 'Super Mario', 'Super Mario Bros. is a platform game developed and published by Nintendo. The successor to the 1983 arcade game, Mario Bros., it was released in Japan in 1985 for the Famicom, and in North America and Europe for the Nintendo Entertainment System (NES) in 1985 and 1987 respectively.', 4890, 2, 1);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (7, 'After Burner II', 'After Burner II is an arcade-style flight game released by Sega in October 1987. It is the second game in the After Burner series. In the game, players fly a F-14 Tomcat jet fighter, gunning down enemies while avoiding incoming fire.', 8799, 2, 2);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (8, 'Alien 3', 'Alien 3 is a side-scrolling platform game in which the player controls Ellen Ripley, the films main character, who progresses through the Fiorina 161 prison colony that was featured in the film. The player can jump, shoot, climb ladders, crawl through tunnels, open doors, and use elevators. Unlike the film, Ripley has a large arsenal of weapons that can be used against the games enemies, consisting of Aliens and Facehuggers. Weapons include pulse rifles, flamethrowers, and grenades. A motion tracker warns the player of nearby enemies', 7499, 2, 2);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (9, 'Batman Returns', 'Batman Returns is the title of a beat em up video game for various platforms based on the film of the same name. The Sega console versions (i.e. Sega Genesis, Sega CD, Master System and Game Gear) were published by Sega while the NES and Super NES versions were developed and published by Konami. The MS-DOS version was published by Konami and developed by Spirit of Discovery. The Amiga version was developed by Denton Designs, and also published by Konami.', 4799, 2, 2);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (10, 'Battletoads', 'Battletoads is a video game media franchise by Rare that began with the original beat em up game Battletoads in 1991. Starring three anthropomorphic toads named after skin conditions, Rash, Zitz, and Pimple, the series was created to rival the Teenage Mutant Ninja Turtles games series. The series as a whole has been quite popular with five installments up to this date and at its apex spawned an animated television pilot.', 14899, 2, 2);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (11, 'Castlevania: Bloodlines', 'Castlevania: Bloodlines[c][d] is a platform game developed and published by Konami for the Sega Genesis. It was the only Castlevania video game released on the Genesis. The games storyline concerns a legendary vampire named Elizabeth Bartley, who is Draculas niece, suddenly appearing in the 20th century. Plotting a sacrificial war in order to bring her uncle back to life, she orchestrates the beginning of World War I.', 7699, 2, 2);
INSERT INTO public.items_item (id, name, description, price, category_id, manufacturer_id) VALUES (12, 'Double Dragon', 'Double Dragon is a 1987 beat em up video game developed by Techn≈çs Japan and distributed in North America and Europe by Taito. The game is a spiritual and technological successor to Technos earlier beat em up, Nekketsu K≈çha Kunio-kun (released outside of Japan by Taito as Renegade), but introduced several additions such as two-player cooperative gameplay and the ability to arm oneself with an enemys weapon after disarming them.', 6799, 2, 2);
